RichTextEditor.registerElement({
    splBlocks: {
        addNodes: function(schema) {
            return schema.spec.nodes.append({
                splBlocks: {
                    content: 'paragraph block*', // don't change this, because assume a situation where:
                    // insert a spl block and type in first line and place cursor at starting of first line and press enter
                    // an empty para will be created at the top inside the spl block,
                    // now place the cursor at the starting of the empty para and press enter again , the expectation is,
                    // thart empty para should move out of the splBlock, and that is what we get
                    // if we change this content , then the above case would not be possible
                    group: 'block',
                    defining: 'true',
                    attrs: {
                        type: {default: undefined}
                    },
                    parseDOM: [
                        {
                            // closeParent: true,
                            tag: 'span.KB_New_Editor_Highlights',
                            getAttrs: function(el) {
                                var bgColor = el.style.backgroundColor;
                                var type = null

                                if (bgColor === 'rgb(252, 237, 158)') {
                                    type = 'notes'
                                } else if (bgColor === 'rgb(255, 198, 151)') {
                                    type = 'alert'
                                } else if (bgColor === 'rgb(193, 225, 244)') {
                                    type = 'info'
                                } else if (bgColor === 'rgb(189, 229, 214)') {
                                    type = 'idea'
                                } else if (bgColor === 'rgb(233, 241, 244)') {
                                    type = 'quote'
                                } else { //if no bgColor matches then either it's bgColor shld be 'rgb(252, 177, 177)' or else also put it as warning node only
                                    type = 'warning'
                                }

                                return {type}
                            }
                        },
                        {
                            tag: 'span.desk-spl-blocks.desk-warning',
                            getAttrs: function() {
                                return {type: 'warning'}
                            }
                        },
                        {
                            tag: 'span.desk-spl-blocks.desk-notes',
                            getAttrs: function() {
                                return {type: 'notes'}
                            }
                        },
                        {
                            tag: 'span.desk-spl-blocks.desk-alert',
                            getAttrs: function() {
                                return {type: 'alert'}
                            }
                        },
                        {
                            tag: 'span.desk-spl-blocks.desk-info',
                            getAttrs: function() {
                                return {type: 'info'}
                            }
                        },
                        {
                            tag: 'span.desk-spl-blocks.desk-idea',
                            getAttrs: function() {
                                return {type: 'idea'}
                            }
                        },
                        {

                            tag: 'span.desk-spl-blocks.desk-quote',
                            getAttrs: function() {
                                return {type: 'quote'}
                            }
                        }
                    ],
                    toDOM: function(node) {

                        var {type} = node.attrs
                        var imgSrc;

                        if(type === 'warning') {
                            imgSrc = 'https://localimg.zohostatic.com/zde/static/images/caution.png'
                        } else if(type === 'notes') {
                            imgSrc = 'https://localimg.zohostatic.com/zde/static/images/file.png'
                        } else if(type === 'alert') {
                            imgSrc = 'https://localimg.zohostatic.com/zde/static/images/exclamation.png'
                        } else if(type === 'info') {
                            imgSrc = 'https://localimg.zohostatic.com/zde/static/images/info.png'
                        } else if(type === 'idea') {
                            imgSrc = 'https://localimg.zohostatic.com/zde/static/images/lights.png'
                        } else if(type === 'quote') {
                            imgSrc = 'https://localimg.zohostatic.com/zde/static/images/quote.png'
                        }

                        return [
                            'div', {} , [
                                'span', {class: 'desk-spl-blocks desk-' + type}, [
                                    'img', { src: imgSrc, rteSplBlockImg: '' }
                                ],[
                                    'div', 0 // We actually want the second child of span tag as a p tag, inorder to put pTag we need to put a hole(0) in DOMOutputSpec as the second child, but since it is not possible to put 0 alone as the second child we have simply put div tag as the second child, as a result p tag will be the child of this div tag
                                ]
                            ]
                        ]
                    }
                }
            })
        },

        registerCommand: function(view) {
            view.registerCommand({
                insertSplBlock: function(blockType) {
                    var view = this.editorView;
                    var attrs = {}
                    var { $from, $to } = view.state.selection
            
                    if(blockType === 'warning') {
                        attrs.type = 'warning'
                    } else if(blockType === 'notes') {
                        attrs.type = 'notes'
                    } else if(blockType === 'alert') {
                        attrs.type = 'alert'
                    } else if(blockType === 'info') {
                        attrs.type = 'info'
                    } else if(blockType === 'idea') {
                        attrs.type = 'idea'
                    } else if(blockType === 'quote') {
                        attrs.type = 'quote'
                    }
            
                    var wrappers = [ { type: view.state.schema.nodes.splBlocks, attrs: attrs }]
                    var tr = view.state.tr.wrap($from.blockRange($to), wrappers)
                    view.dispatch(tr)
                    view.focus();

                },

                removeSplBlock: function()  {
                    let state = this.editorView.state
                    let {$from, $to} = state.selection
                    
                    let target = RichTextEditor.PMExports.prosemirrorTransform.liftTarget($from.blockRange($to))
                    this.editorView.dispatch(state.tr.lift($from.blockRange($to), target))
                },
                
                replaceSplBlock: function(blockType) {
                    this.commands.removeSplBlock()
                    this.commands.insertSplBlock(blockType)
                }
            })
        }
    }
})

function makeTableCellStyleProperAsEmpty(node, pos, view, tr) {
    let findChildrenByType = RichTextEditor.PMExports.prosemirrorUtils.findChildrenByType
    let tableCellArray = findChildrenByType(node, view.state.schema.nodes.table_cell)
    tableCellArray.forEach((arrayItm) => {
        tr = tr.setNodeMarkup(pos + 1 + arrayItm.pos, arrayItm.node.type, {// here pos is the relative position of table_cell from it's parent 
        // node (table), so always add table node's position in the document and do +1 and then add this position of table_cell
            ...arrayItm.node.attrs, extraAttrs: {
                ...arrayItm.node.attrs.extraAttrs, style: null
            }
        })
    })
}

